"use strict";var card=document.querySelectorAll(".card"),cardArrow=document.getElementById("card__arrow");console.log(card[0].classList);var slideCard=function(r){"next"===r?(card[0].classList.add("ToLeft"),card[1].classList.add("ToLeft")):(card[0].classList.remove("ToLeft"),card[1].classList.remove("ToLeft"))};cardArrow.addEventListener("click",(function(r){slideCard(r.target.dataset.direction)}));